import React, { Component } from "react";
import { AwesomeButton } from "react-awesome-button";
import "./Button.css";

class CompanyOfferManage extends Component {
  render() {
    // const { missions, isLoaded } = this.props;
    console.log("c'est là", this.props);
    const { isLoaded, missions } = this.props;
    return (
      <div>
        <div>
          {!isLoaded ? (
            <p> loading.. </p>
          ) : (
            missions.map(e => (
              <div>
                <h2> {e.titleMission}</h2>
                <h3>
                  {e.dateStart} - {e.dateEnd}
                </h3>
                <p> {e.description}</p>
              </div>
            ))
          )}
        </div>

        <AwesomeButton type="primary" className="aws-btn edit">
          Modifier
        </AwesomeButton>
        <br />
        <AwesomeButton type="primary" className="aws-btn remove">
          Supprimer
        </AwesomeButton>
        <br />
        <hr align="center" width="50%" color="midnightblue" size="1" />
        <AwesomeButton type="primary" className="aws-btn validate">
          Valider ma team
        </AwesomeButton>
      </div>
    );
  }
}

export default CompanyOfferManage;
