          <p> loading.. </p>
        ) : (
          missions.map((e, index) => (
            <div key={index}>
              {titleMissions}
              <br />
              {start}
              <br />
              {end}
              <br />
              {descrip}
            </div>
          ))
        )}
      </div>
    );
  }
}

export default OfferView;
