import React from "react";
import PropTypes from "prop-types";
import ClassNames from "classnames";

const propTypes = {
  className: PropTypes.string,
  color: PropTypes.string,
  outline: PropTypes.bool,
  children: PropTypes.node,
  tag: PropTypes.oneOfType([PropTypes.string, PropTypes.func]),
  disabled: PropTypes.bool,
  onClick: PropTypes.func
};

const defaultProps = {
  className: null,
  color: "secondary",
  outline: false,
  children: null,
  tag: "button",
  disabled: false,
  onClick: null
};

class Button extends React.Component {
  constructor(props) {
    super(props);

    this.onClick = this.onClick.bind(this);
  }

  onClick(e) {
    const { disabled, onClick } = this.props;

    if (disabled) {
      e.preventDefault();
      return;
    }

    if (onClick) onClick();
  }

  render() {
    const { className, color, outline, children, tag, ...attr } = this.props;

    delete attr.onClick;

    const classes = ClassNames(
      className,
      "btn",
      color && !outline ? `btn-${color}` : null,
      color && outline ? `btn-outline-${color}` : null
    );

    let Tag = tag;
    if (attr.href && Tag === "button") Tag = "a";

    if (!attr.type && Tag === "button") attr.type = "button";

    return (
      <Tag
        {...attr}
        onClick={e => {
          this.onClick(e);
        }}
        className={classes}
      >
        {children}
      </Tag>
    );
  }
}

Button.propTypes = propTypes;
Button.defaultProps = defaultProps;

export default Button;
