import React, { Component } from "react";
import Button from "./Button";

const colors = [
  "Primary",
  "Secondary",
  "Success",
  "Info",
  "Warning",
  "Danger",
  "Link"
];

class CompanyOffers extends Component {
  render() {
    return (
      <div>
        <h1> Mes Missions </h1>
        {colors.map(color => (
          <Button
            key={`outline-${color}`}
            outline
            color={color.toLowerCase()}
            className="m-1"
          >
            {color}
          </Button>
        ))}
      </div>
    );
  }
}

export default CompanyOffers;
