import React, { Component } from "react";

import logo from "../../../img/Logo.png";

import "./OnePage.css";
import Button from "@material-ui/core/Button";
import PartieEntreprise from "./PartieEntreprise";

export default class OnePage extends Component {
  state = {
    openTrainee: false,
    openCompany: false,
    button: "fermer"
  };

  traineeOpenConnexion = () => {
    this.setState({
      openTrainee: true
    });
  };

  companyOpenConnexion = () => {
    this.setState({
      openCompany: true
    });
  };

  handleCloseTrainee = () => {
    this.setState({ openTrainee: false });
  };

  handleCloseCompany = () => {
    this.setState({ openCompany: false });
  };

  render() {
    return (
      // Header //
      <div>
        <div className="Header">
          <div className="Header-overlay">
            <img className="logo" src={logo} />
            <p className="connexion">
              <span>Se connecter </span>|<span> S'inscrire</span>
            </p>

            <h1 className="textCentral">
              La 1ère plateforme de teams <br /> d'étudiants-stagiaires
            </h1>
            <Button
              variant="contained"
              className="buttonConnexion"
              onClick={this.companyOpenConnexion}
            >
              Déposer une offre de stage
            </Button>
            <Button variant="contained" className="buttonConnexion2" onClick={this.traineeOpenConnexion}>
              Postuler à une offre
            </Button>
          </div>
        </div>
        <PartieEntreprise />
      </div>
    );
  }
}
