import React, { Component } from "react";
import { connect } from "react-redux";
import axios from "axios";
import Typography from "@material-ui/core/Typography";
import StudentApplication from "./StudentApplication";
import { SMALL } from "./studentConstant";
import "./ViewStudent.css";

const modeSelect = "SELECT";
const modeRefuse = "REFUSE";

class CompanyApplicationsList extends Component {
  state = {
    trainee: [],
    id: 1,
    isLoaded: false,
    open: "",
    title: "",
    content: "",
    button: ""
  };

  componentDidMount() {
    const { mode } = this.props;
    const idCompany = sessionStorage.getItem("token");
    console.log("idcompany", idCompany);
    axios
      .get(`http://localhost:3001/application/${idCompany}/${mode}/mytrainee`)
      .then(res => {
        console.log(res.data);
        this.setState({
          trainee: res.data,
          isLoaded: true,
          open: true,
          title: `Trainee correctly added !`,
          content: `Vous avez préselectionné ${res.data}`,
          button: `Se connecter`
        });
      })
      .catch(error => {
        if (error.response.status === 400) {
          this.setState({
            open: true,
            title: `user already exists`,
            content: `Cette adresse mail existe déjà, connectez-vous!`,
            button: `Se connecter`
          });
        } else {
          this.setState({
            open: true,
            title: `Oups une erreur s'est produite`,
            content: `Veuillez recommencer s'il-vous-plait`,
            button: `Fermer`
          });
        }
      });
  }

  compareMissions = (a, b) => {
    return a - b;
  };

  render() {
    const { trainee, isLoaded } = this.state;
    const { mode } = this.props;
    console.log(trainee.data);
    return (
      <div>
        {isLoaded &&
          trainee.data.sort(this.compareMissions).map(element => (
            <div>
              <Typography variant="h2" component="h3">
                {element.titleMission}
              </Typography>
              <div className="blocList">
                {element.dataApplications.map(e => (
                  <StudentApplication
                    firstname={e.Trainee.firstname}
                    town={e.Trainee.town}
                    pictures={e.Trainee.pictures}
                    address={e.Trainee.address}
                    postalCode={e.Trainee.postalCode}
                    size={SMALL}
                    missionId={element.mission_id}
                    traineeId={e.Trainee.id}
                    modeSelect={modeSelect}
                    modeRefuse={modeRefuse}
                    mode={mode}
                  />
                ))}
              </div>
            </div>
          ))}
      </div>
    );
  }
}

const mapStateToProps = state => ({
  idCompany: state.company.id
});

export default connect(mapStateToProps)(CompanyApplicationsList);
