import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import { MakeCompletedUrl } from "../../tools";

const styles = theme => ({
  row: {
    border: "1px solid black"
  },
  paper: {
    ...theme.mixins.gutters(),
    padding: "0",
    margin: "15px",
    width: "250px"
  },
  paperFull: {
    ...theme.mixins.gutters(),
    padding: "0",
    margin: "15px",
    width: "500px"
  },
  list: {
    listStyle: "none",
    padding: "10px",
    textAlign: "center"
  },
  image: {
    borderRadius: "100%",
    height: "150px",
    width: "150px",
    padding: "10px"
  },
  address: {
    margin: "10px"
  }
});

const StudentView = props => {
  const {
    classes,
    pictures,
    address,
    postalCode,
    firstname,
    town,
    size,
    phone,
    dateStart,
    dateEnd,
    titre,
    descriptionTrainee,
    school,
    lastname,
    email,
    LevelStudy,
    age
  } = props;
  // console.log("StudentView", dateStart, dateEnd);

  switch (size) {
    case "SMALL": {
      return (
        <div>
          <Paper className={classes.paper} elevation={2}>
            <p>Size SMALL</p>
            <ul className={classes.list}>
              <li>
                <img
                  className={classes.image}
                  src={
                    pictures !== null
                      ? MakeCompletedUrl(`${pictures}`)
                      : MakeCompletedUrl("public/photoProfile/PhotoProfil.jpg")
                  }
                  alt=""
                />
              </li>
              <li>{firstname}</li>
              <li>
                {titre !== null ? titre : "Poste recherché : à compléter"}
              </li>
              <li>
                début :
                {dateStart
                  ? new Date(dateStart).toLocaleDateString()
                  : "à compléter"}
              </li>
              <li>
                fin:
                {dateEnd
                  ? new Date(dateEnd).toLocaleDateString()
                  : "à compléter"}
              </li>
              <li className={classes.address}>
                {town !== null ? town : "Ville : à compléter"}
              </li>
            </ul>
          </Paper>
        </div>
      );
    }
    case "FULL_RESTRICTED": {
      return (
        <div className="bloc-trainee-profil">
          <div className="bandeau-trainee-profil">
            <div className="user-photo">
              <img
                src={
                  pictures !== null
                    ? MakeCompletedUrl(`${pictures}`)
                    : MakeCompletedUrl("public/photoProfile/PhotoProfil.jpg")
                }
                alt=""
              />
            </div>
          </div>
          <p>{firstname}</p>
          <p>{titre !== null ? titre : "Poste recherché : à compléter"}</p>
          <div>
            <img src="" alt="" />
            <p>{age}</p>
          </div>
          <p>{school !== null ? school : "École : à compléter"}</p>
          <p>
            début :
            {dateStart
              ? new Date(dateStart).toLocaleDateString()
              : "à compléter"}
          </p>
          <p>
            fin:
            {dateEnd ? new Date(dateEnd).toLocaleDateString() : "à compléter"}
          </p>
          <p className={classes.address}>
            {town !== null ? town : "Ville : à compléter"}
          </p>
          <p>
            {descriptionTrainee !== null
              ? descriptionTrainee
              : "description : à compléter"}
          </p>
          <p>
            {LevelStudy !== null ? LevelStudy : "Niveau d'étude : à compléter"}
          </p>
        </div>
      );
    }
    case "FULL": {
      return (
        <div>
          <Paper className={classes.paperFull} elevation={2}>
            <p>Size FULL</p>
            <ul className={classes.list}>
              <li>
                <img
                  className={classes.image}
                  src={
                    pictures !== null
                      ? MakeCompletedUrl(`${pictures}`)
                      : MakeCompletedUrl("public/photoProfile/PhotoProfil.jpg")
                  }
                  alt=""
                />
              </li>
              <li>{firstname}</li>
              <li>{lastname}</li>
              <li>
                {titre !== null ? titre : "Poste recherché : à compléter"}
              </li>
              <li>{school !== null ? school : "École : à compléter"}</li>
              <li>
                début :
                {dateStart
                  ? new Date(dateStart).toLocaleDateString()
                  : "à compléter"}
              </li>
              <li>
                fin:
                {dateEnd
                  ? new Date(dateEnd).toLocaleDateString()
                  : "à compléter"}
              </li>
              <li className={classes.address}>{address}</li>
              <li>
                {postalCode !== null ? postalCode : "Code postal : à compléter"}
              </li>
              <li> {town !== null ? town : "Ville : à compléter"}</li>
              <li>
                {descriptionTrainee !== null
                  ? descriptionTrainee
                  : "Description: à compléter"}
              </li>
              <li>
                {LevelStudy !== null
                  ? LevelStudy
                  : "Niveau d'étude : à compléter"}
              </li>
              <li>{phone !== null ? phone : "Télephone : à compléter"}</li>
              <li>{email}</li>
            </ul>
          </Paper>
        </div>
      );
    }
    default:
      break;
  }
};

export default withStyles(styles)(StudentView);
