import React, { Component } from "react";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import Button from "@material-ui/core/Button";
import StudentView from "./StudentView";
import { FULL } from "./studentConstant";

class StudentProfilView extends Component {
  state = {
    openState: true
  };

  handleCloseFull = () => {
    console.log("close");
    this.setState({ openState: false });
  };

  render() {
    console.log(this.props.open);
    const { openState } = this.state;
    const { open } = this.props;
    return (
      <div>
        {open ? (
          <Dialog
            open={openState}
            aria-labelledby="alert-dialog-title"
            aria-describedby="alert-dialog-description"
          >
            <DialogTitle id="customized-dialog-title" />
            <DialogContent>
              <DialogContentText id="alert-dialog-description">
                <StudentView {...this.props} size={FULL} />
              </DialogContentText>
            </DialogContent>
            <DialogActions>
              <Button onClick={this.handleCloseFull}>fermer</Button>
            </DialogActions>
          </Dialog>
        ) : null}
      </div>
    );
  }
}
export default StudentProfilView;

