import React from "react";
import PropTypes from "prop-types";
import {
  withStyles,
  createMuiTheme,
  MuiThemeProvider
} from "@material-ui/core/styles";
import MobileStepper from "@material-ui/core/MobileStepper";
import SwipeableViews from "react-swipeable-views";
import { autoPlay } from "react-swipeable-views-utils";

import amber from "@material-ui/core/colors/amber";
import TraineeSlide from "./TraineeSlide";
import "./traineeCarrousel.css";

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

const tutorialSteps = [
  {
    subtitle: `« Ce qui me plaît est l’idée de nous engager à plusieurs sur un projet commun »`,
    imgPath: "./img/slide1.png"
  },
  {
    subtitle: `« Je pourrais me focaliser sur un savoir spécifique que j’apporterais à la team »`,
    imgPath: "./img/slide2.png"
  },
  {
    subtitle: `« On vit en réseau, on partage des communautés d’intérêts, c’est d’être isolé qui n’est pas la norme »`,
    imgPath: "./img/slide3.png"
  }
];

const styles = theme => ({
  root: {
    maxWidth: "100%",
    flexGrow: 1
  },
  img: {
    height: 550,
    display: "block",
    maxWidth: "100%",
    overflow: "hidden",
    width: "100%"
  },
  mobileStepper: {
    display: "flex",
    justifyContent: "center",
    position: "absolute",
    bottom: 0,
    backgroundColor: "rgba(0, 0, 0, 0)"
  },
  bloc: {
    position: "relative"
  }
});

class TraineeCarrousel extends React.Component {


  render() {
    const { classes } = this.props;

    return (
      <div className={classes.root}>
        <MuiThemeProvider theme={theme}>
          <div className={classes.bloc}>
            <AutoPlaySwipeableViews
              onChangeIndex={this.handleStepChange}
              enableMouseEvents
              interval={5000}
            >
              {tutorialSteps.map(element => (
                <TraineeSlide img={element.imgPath} text={element.subtitle} />
              ))}
            </AutoPlaySwipeableViews>
          </div>
        </MuiThemeProvider>
      </div>
    );
  }
}
const theme = createMuiTheme({
  palette: {
    primary: amber
  },
  overrides: {
    MuiMobileStepper: {
      dotActive: {
        backgroundColor: "#fdad17"
      }
    }
  }
});

TraineeCarrousel.propTypes = {
  classes: PropTypes.object.isRequired,
  theme: PropTypes.object.isRequired
};

export default withStyles(styles, { withTheme: true })(TraineeCarrousel);
