import React, { Component } from "react";
import CompanyPresentationFull from "./CompanyPresentationFull";
import companyIcon from "../../img/three-buildings.png";

class CompanyPresentation extends Component {
  state = {
    open: false
  };

  clickClose = () => {
    this.setState({ open: false });
  };

  clickMissionSmall = () => {
    console.log("open");
    this.setState({ open: true });
  };

  render() {
    const {
      titleMission,
      company,
      introduction,
      dateStart,
      dateEnd,
      town
    } = this.props;
    const { open } = this.state;
    return (
      <div>
        <div onClick={() => this.clickMissionSmall()}>
          <div className="icon-and-text">
            <div className="img-student-view">
              <img src={companyIcon} alt="entreprise" />
            </div>
            <p className="criteres_big">{titleMission}</p>
          </div>

          <p>{titleMission}</p>
          <p>{company}</p>
          <p>d√©but: {new Date(dateStart).toLocaleDateString()}</p>
          <p>fin: {new Date(dateEnd).toLocaleDateString()}</p>
          <p>{town}</p>
          <p>{introduction}</p>
        </div>
        <CompanyPresentationFull
          open={open}
          close={this.clickClose}
          {...this.props}
        />
      </div>
    );
  }
}

export default CompanyPresentation;
