import React, { Component } from "react";
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import "./traineeProfile.css";

class TraineeProfile extends Component {
  state = {
    // id: 9,
    image: "",
    lastname: "",
    firstname: "",
    email: "",
    pictures: "",
    phone: "",
    address: "",
    town: "",
    postalCode: "",
    selectedFile: null
  };

  componentDidMount() {
    const id = sessionStorage.getItem("token");
    axios
      .post("http://localhost:3001/trainee/profile?id={id}", { id })
      .then(response => {
        console.log(response);
        this.setState({
          data: response.data
        });
      })
      .catch(error => {
        console.log(error.response);
      });
  }

  onSubmit = e => {
    e.preventDefault();
    // const { id } = this.state;
    const id = sessionStorage.getItem("token");
    axios
      .put("http://localhost:3001/trainee/profile", {
        id,
        lastname: e.target.lastname.value,
        firstname: e.target.firstname.value,
        phone: e.target.phone.value,
        address: e.target.address.value,
        town: e.target.town.value,
        postalCode: e.target.postalCode.value
      })
      .then(response => {
        console.log(response);
      })
      .catch(error => {
        console.log(error.response);
      });
    this.uploadPhoto();
  };

  uploadPhoto = () => {
    // e.preventDefault();
    const id = sessionStorage.getItem("token");
    console.log(this.state.selectedFile);
    const formData = new FormData();
    formData.append(
      "avatar",
      this.state.selectedFile,
      this.state.selectedFile.name
    );
    axios.post(`http://localhost:3001/trainee/uploadphoto/${id}`, formData);
  };

  fileChangedHandler = event => {
    const fr = new FileReader();

    fr.onload = a => {
      this.setState({ image: a.currentTarget.result });
    };
    fr.readAsDataURL(document.querySelector('input[type="file"]').files[0]);

    this.setState({ selectedFile: event.target.files[0] });
    console.log("okkkkk");
  };

  render() {
    console.log(this.state.data);
    if (this.state.data == null) {
      return <div>Loading</div>;
    }
    return (
      <div>
        <h1>Complète ton profile</h1>
        <div className="createForm">
          <form onSubmit={this.onSubmit}>
            <div>
              <label for="file-input">
                {this.state.data.pictures !== null ? (
                  <div>
                    <img
                      src={
                        this.state.image ||
                        `http://localhost:3001/${this.state.data.pictures}`
                      }
                      width="100"
                      height="100"
                      alt=" Profile"
                    />
                  </div>
                ) : (
                  <img
                    src={
                      this.state.image ||
                      "http://localhost:3001/public/photoProfile/PhotoProfil.jpg"
                    }
                    width="100"
                    height="100"
                    alt=" default Profile"
                  />
                )}
              </label>
              <input
                id="file-input"
                type="file"
                onChange={this.fileChangedHandler}
                hidden
              />
            </div>
            <TextField
              type="text"
              className="textField"
              name="firstname"
              placeholder="Prénom"
              defaultValue={this.state.data.firstname}
              margin="normal"
              variant="outlined"
              required
            />
            <TextField
              type="text"
              className="textField"
              name="lastname"
              placeholder="Nom"
              defaultValue={this.state.data.lastname}
              margin="normal"
              variant="outlined"
              required
            />

            <TextField
              disabled
              type="email"
              className="textField"
              name="email"
              placeholder="Email"
              defaultValue={this.state.data.email}
              margin="normal"
              variant="outlined"
              required
            />

            <TextField
              type="password"
              className="textField"
              name="password"
              placeholder="Mot de passe"
              margin="normal"
              variant="outlined"
            />
            <TextField
              type="text"
              className="textField"
              name="phone"
              placeholder="Phone"
              defaultValue={this.state.data.phone}
              margin="normal"
              variant="outlined"
            />
            <TextField
              type="text"
              className="textField"
              name="address"
              placeholder="Adress"
              defaultValue={this.state.data.address}
              margin="normal"
              variant="outlined"
            />
            <TextField
              type="text"
              className="textField"
              name="town"
              placeholder="Ville"
              defaultValue={this.state.data.town}
              margin="normal"
              variant="outlined"
            />
            <TextField
              type="text"
              className="textField"
              name="postalCode"
              placeholder="Postal Code"
              defaultValue={this.state.data.postalCode}
              margin="normal"
              variant="outlined"
            />
            <Button
              variant="contained"
              className="buttonCreateForm"
              type="submit"
            >
              {`Enregistrer`}
            </Button>
          </form>
        </div>
      </div>
    );
  }
}

export default TraineeProfile;
