import React, { Component } from 'react';
import axios from 'axios'

class StudentCreateAccount extends Component {

    state = {
        firstName: "",
        lastName: "",
        email: "",
        password: "",
    }

    onChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }
    onSubmit = (e) => {
        e.preventDefault()
        const postFormStudent = [{
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            email: this.state.email,
            password: this.state.password,
        }]
        console.log(postFormStudent)
        // axios.post('http://localhost:3002/registerTeam', this.state.formStudent)
        console.log(this.state.firstName)
    }
    render() {
        return (

        <div>
            <form method="post" onSubmit={this.onSubmit}>
                <div><input type="text" name="firstName" placeholder="PrÃ©nom" onChange={this.onChange}/></div>
                <div><input type="text" name="lastName" placeholder="Nom" onChange={this.onChange}/></div>
                <div><input type="email" name="email" placeholder="Email" onChange={this.onChange}/></div>
                <div><input type="password" name="password" placeholder="Mot de passe" onChange={this.onChange}/></div>
                <button type="submit">S'inscrire</button>
            </form>
        </div>
        );
    }
}

export default StudentCreateAccount;
