import React from "react";
import PropTypes from "prop-types";
import {
  withStyles,
  createMuiTheme,
  MuiThemeProvider
} from "@material-ui/core/styles";
import MobileStepper from "@material-ui/core/MobileStepper";
import SwipeableViews from "react-swipeable-views";
import { autoPlay } from "react-swipeable-views-utils";
import Button from "@material-ui/core/Button";
import amber from "@material-ui/core/colors/amber";

const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

const tutorialSteps = [
  {
    subtitle: `« Ce qui me plaît est l’idée de nous engager à plusieurs sur un projet commun »`,
    imgPath: "./images/Slide1.jpg"
  },
  {
    subtitle: "Rééquilibrage",
    imgPath: "./images/Slide2.jpg"
  },
  {
    subtitle: "Pratique et détente",
    imgPath: "./images/Slide3.jpg"
  }
];

const styles = theme => ({
  root: {
    maxWidth: "100%",
    flexGrow: 1
  },
  img: {
    height: 550,
    display: "block",
    maxWidth: "100%",
    overflow: "hidden",
    width: "100%"
  },
  mobileStepper: {
    display: "flex",
    justifyContent: "center",
    position: "absolute",
    bottom: 0,
    backgroundColor: "rgba(0, 0, 0, 0)"
  },
  bloc: {
    position: "relative"
  }
});

class TraineeCarrousel extends React.Component {
  state = {
    activeStep: 0
  };

  divStyle = imgSrc => ({
    backgroundImage: `url(${imgSrc})`
  });

  handleStepChange = activeStep => {
    this.setState({ activeStep });
  };

  render() {
    const { classes } = this.props;
    const { activeStep } = this.state;
    const maxSteps = tutorialSteps.length;

    return (
      <div className={classes.root}>
        <MuiThemeProvider theme={theme}>
          <div className={classes.bloc}>
            <AutoPlaySwipeableViews
              onChangeIndex={this.handleStepChange}
              enableMouseEvents
            >
              {tutorialSteps.map((element, index) => (
                <div
                  key={element.title}
                  className="carrousel"
                  style={this.divStyle(element.imgPath)}
                >
                  <div className="carrouselInfo">
                    <h2 className="title-slide">{element.title}</h2>
                    <div className="underline-style" />
                    <h3 className="subtitle-slide">{element.subtitle}</h3>
                    <Button
                      className="button-carrousel"
                      variant="contained"
                      color="primary"
                    >
                      En savoir plus
                    </Button>
                  </div>
                </div>
              ))}
            </AutoPlaySwipeableViews>

            <MobileStepper
              steps={maxSteps}
              activeStep={activeStep}
              className={classes.mobileStepper}
            />
          </div>
        </MuiThemeProvider>
      </div>
    );
  }
}
const theme = createMuiTheme({
  palette: {
    primary: amber
  },
  overrides: {
    MuiMobileStepper: {
      dotActive: {
        backgroundColor: "#fdad17"
      }
    }
  }
});

TraineeCarrousel.propTypes = {
  classes: PropTypes.object.isRequired,
  theme: PropTypes.object.isRequired
};

export default withStyles(styles, { withTheme: true })(TraineeCarrousel);
