import React, { Component } from "react";
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import classNames from "classnames";
import { withStyles } from "@material-ui/core/styles";

const styles = theme => ({
  container: {
    display: "flex",
    flexWrap: "wrap"
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit
  },
  dense: {
    marginTop: 16
  },
  menu: {
    width: 200
  }
});

class StudentCreateAccount extends Component {
  state = {
    firstname: null,
    lastname: null,
    email: null,
    password: null,
    response: undefined
  };

  onChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  onSubmit = e => {
    e.preventDefault();
    const postFormStudent = {
      firstname: this.state.firstname,
      lastname: this.state.lastname,
      email: this.state.email,
      password: this.state.password
    };
    console.log(postFormStudent);
    axios.post("http://localhost:3001/trainee", postFormStudent).then(data =>
      this.setState(
        {
          response: data.data.message
        },
        () => alert(this.state.response)
      )
    );
  };
  render() {
    const { classes } = this.props;
    return (
        <form
          method="post"
          onSubmit={this.onSubmit}
        >
          <TextField
            type="text"

            name="firstname"
            placeholder="PrÃ©nom"
            onChange={this.onChange}
          />
          <TextField
            type="text"

            name="lastname"
            placeholder="Nom"
            onChange={this.onChange}
          />

          <TextField
            type="email"

            name="email"
            placeholder="Email"
            onChange={this.onChange}
          />

          <TextField
            type="password"

            name="password"
            placeholder="Mot de passe"
            onChange={this.onChange}
          />
          <button type="submit">S'inscrire</button>
        </form>
    );
  }
}

export default withStyles(styles)(StudentCreateAccount);
