import React, { Component } from "react";
import axios from "axios";
import TextField from "@material-ui/core/TextField";
import { withStyles } from "@material-ui/core/styles";
import PropTypes from "prop-types";

const styles = theme => ({
  container: {
    display: "flex",
    flexDirection: 'column',
    flexWrap: "wrap",
    width: 'calc(100px + 10em)',
    margin: 'auto',
    // border: '1px solid black'
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit
  }
});

class StudentCreateAccount extends Component {
  state = {
    firstname: null,
    lastname: null,
    email: null,
    password: null,
    response: undefined
  };

  onChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  onSubmit = e => {
    e.preventDefault();
    const postFormStudent = {
      firstname: this.state.firstname,
      lastname: this.state.lastname,
      email: this.state.email,
      password: this.state.password
    };
    console.log(postFormStudent);
    axios.post("http://localhost:3001/trainee", postFormStudent).then(data =>
      this.setState(
        {
          response: data.data.message
        },
        () => alert(this.state.response)
      )
    );
  };
  render() {
    const { classes } = this.props;
    return (
      <form
        method="post"
        className={classes.container}
        onSubmit={this.onSubmit}
      >
        <TextField
          type="text"
          className={classes.textField}
          name="firstname"
          placeholder="Prénom"
          onChange={this.onChange}
          margin="normal"
          variant="outlined"
        />
        <TextField
          type="text"
          className={classes.textField}
          name="lastname"
          placeholder="Nom"
          onChange={this.onChange}
          margin="normal"
          variant="outlined"
        />

        <TextField
          type="email"
          className={classes.textField}
          name="email"
          placeholder="Email"
          onChange={this.onChange}
          margin="normal"
          variant="outlined"
        />

        <TextField
          type="password"
          className={classes.textField}
          name="password"
          placeholder="Mot de passe"
          onChange={this.onChange}
          margin="normal"
          variant="outlined"
        />
        <button type="submit">S'inscrire</button>
      </form>
    );
  }
}

StudentCreateAccount.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(StudentCreateAccount);
