import React from "react";
const styles = theme => ({
  mobileStepper: {
    position: "static",
    backgroundColor: "rgba(0, 0, 0, 0)"
  },
  bloc: {
    display: "flex",
    flexDirection: "column"
  },
  carrousel: {
    position: "relative"
  }
});
const TraineeSlide = ({ text, img }) => (
  <div>
    <div>
      <img src={img} alt={text} />
    </div>
    <h3 className="subtitle-slide">{text}</h3>
  </div>
);

export default TraineeSlide;
