import React from "react";

const StudentView = props => {
  const { firstname, lastname, size } = props;
  switch (size) {
    case "SMALL": {
      return (
        <div>
          <p> {firstname} </p>
        </div>
      );
    }
    case "FULL_RESTRICTED": {
      return (
        <div>
          <p> {lastname} </p>
        </div>
      );
    }
    case "FULL": {
      return (
        <div>
          <p> {firstname} </p>
          <p> {lastname} </p>
        </div>
      );
    }
    default:
      break;
  }
};

export default StudentView;
