import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";

const styles = theme => ({
  row: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "center"
  },
  root: {
    ...theme.mixins.gutters(),
    padding: "0",
    // paddingTop: theme.spacing.unit * 2,
    // paddingBottom: theme.spacing.unit * 2,
    // border: "1px solid green",
    margin: "10px",
    width: "250px",
    height: "310px"
  },
  list: {
    listStyle: "none",
    padding: "0",
    textAlign: "center"
  },
  image: {
    borderRadius: "100%",
    height: "150px",
    width: "150px",
    padding: "10px 0"
  },
  adress: {
    margin: "10px"
  }
});

const StudentView = props => {
  const { classes, image, firstname, lastname, town, id, size } = props;
  switch (size) {
    case "SMALL": {
      return (
        <div className={classes.row}>
          <Paper className={classes.root} elevation={2}>
            <ul className={classes.list}>
              {/* <li>{name.trainee.lastname}</li> */}
              <li>
                <img
                  className={image}
                  src={name.Trainee.pictures}
                  alt=""
                />
              </li>
              <li>{name.Trainee.firstname}</li>
              <li>{name.Trainee.email}</li>
              <li className={classes.adress}>
                {" "}
                {name.Trainee.address} - {name.Trainee.postalCode}{" "}
                {name.Trainee.town}
              </li>
            </ul>
          </Paper>
          )
        </div>
      );
    }
    case "FULL_RESTRICTED": {
      return (
        <div>
          <p> {lastname} </p>
        </div>
      );
    }
    case "FULL": {
      return (
        <div>
          <p> {firstname} </p>
          <p> {town} </p>
          <p> {id} </p>
        </div>
      );
    }
    default:
      break;
  }
};

export default withStyles(styles)(StudentView);
