import React, { Component } from "react";
import axios from "axios";
import { MakeCompletedUrl, ConvertDate } from "../../tools";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";

import MenuItem from "@material-ui/core/MenuItem";
import { createMuiTheme, MuiThemeProvider } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import StudentView from "../CompanyApplication/StudentView";
import { FULL_RESTRICTED } from "../CompanyApplication/studentConstant";
import RemoveEye from "@material-ui/icons/RemoveRedEye";
import "./traineeProfile.css";

const theme = createMuiTheme({
  palette: {
    primary: blue
  }
});

class TraineeProfile extends Component {
  state = {
    selectedFile: null,
    openTrainee: false,
    button: "fermer"
  };

  traineeOpenConnexion = () => {
    this.setState({
      openTrainee: true
    });
  };

  handleCloseTrainee = () => {
    this.setState({ openTrainee: false });
  };

  handleChange = event => {
    this.setState(prevState => ({
      data: {
        ...prevState.data,
        [event.target.name]: event.target.value
      }
    }));
  };

  componentDidMount() {
    const id = sessionStorage.getItem("token");
    axios
      .get(MakeCompletedUrl(`trainee/profile/${id}`))
      .then(response => {
        // console.log(response.data);
        this.setState({
          data: response.data
        });
      })
      .catch(error => {
        console.log(error.response);
      });
    axios
      .get(MakeCompletedUrl("paradata/levelstudies"))
      .then(response => {
        // console.log(response.data);
        this.setState({
          levelstudies: response.data
        });
      })
      .catch(error => {
        console.log(error.response);
      });
  }

  onSubmit = e => {
    // e.preventDefault();
    // const { id } = this.state;
    const id = sessionStorage.getItem("token");
    axios
      .put(MakeCompletedUrl("trainee/profile"), {
        id,
        firstname: e.target.firstname.value,
        lastname: e.target.lastname.value,
        phone: e.target.phone.value,
        address: e.target.address.value,
        town: e.target.town.value,
        postalCode: e.target.postalCode.value,
        dateBirth: e.target.dateBirth.value,
        school: e.target.school.value,
        titre: e.target.titre.value,
        description: e.target.description.value,
        dateStart: e.target.dateStart.value,
        dateEnd: e.target.dateEnd.value,
        LevelStudyId: e.target.LevelStudyId.value
      })
      .then(response => {
        console.log(response);
      })
      .catch(error => {
        console.log(error.response);
      });
    this.uploadPhoto();
  };

  uploadPhoto = () => {
    // e.preventDefault();
    const id = sessionStorage.getItem("token");
    if (this.state.selectedFile !== null) {
      console.log("uploadphoto", this.state.selectedFile);
      const formData = new FormData();
      formData.append(
        "avatar",
        this.state.selectedFile,
        this.state.selectedFile.name
      );
      axios.post(MakeCompletedUrl(`trainee/uploadphoto/${id}`), formData);
    }
  };

  fileChangedHandler = event => {
    const fr = new FileReader();

    fr.onload = a => {
      this.setState({ image: a.currentTarget.result });
    };
    fr.readAsDataURL(document.querySelector('input[type="file"]').files[0]);

    this.setState({ selectedFile: event.target.files[0] });
    console.log("okkkkk");
  };

  date(data) {
    const date = new Date(data);
    let day = date.getDate();
    if (day < 10) {
      day = "0" + day;
    }
    let month = date.getMonth() + 1;
    if (month < 10) {
      month = "0" + month;
    }
    const year = date.getFullYear();
    return `${year}-${month}-${day}`;
  }

  handleOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  render() {
    const { data, openTrainee, button } = this.state;
    if (this.state.data == null || this.state.levelstudies == null) {
      return <div>Loading</div>;
    }
    return (
      <MuiThemeProvider theme={theme}>
        <div>
          <div className="traineeProfileTitleBackGroud">
            <div className="traineeProfileTitle">
              <h1 className="traineeProfileTextProfile">Profil</h1>
              <h2 className="traineeProfileText">
                Compléte ton profil avant de postuler à toute offre de stage
              </h2>
            </div>
          </div>
          <div className="createForm">
            <form onSubmit={this.onSubmit}>
              <Grid
                container
                direction="row"
                justify="flex-start"
                alignItems="center"
              >
                <Grid item xs={6}>
                  <div>
                    <label for="file-input">
                      {this.state.data.pictures !== null ? (
                        <div>
                          <img
                            className="traineeProfileImage"
                            src={
                              this.state.image ||
                              MakeCompletedUrl(`${this.state.data.pictures}`)
                            }
                            alt=" Profile"
                          />
                        </div>
                      ) : (
                        <img
                          className="traineeProfileImage"
                          src={
                            this.state.image ||
                            MakeCompletedUrl(
                              "public/photoProfile/PhotoProfil.jpg"
                            )
                          }
                          alt=" default Profile"
                        />
                      )}
                    </label>
                    <input
                      id="file-input"
                      type="file"
                      onChange={this.fileChangedHandler}
                      hidden
                    />
                  </div>
                </Grid>
                <Grid item xs={3}>
                  <Button
                    color="primary"
                    variant="contained"
                    className="traineeProfileButton"
                    onClick={this.traineeOpenConnexion}
                  >
                    Aperçu profil
                    <RemoveEye className="traineeProfileRemoveRedEye" />
                  </Button>
                </Grid>
                <Grid item xs={3}>
                  <Button variant="contained" type="submit">
                    {`Sauvegarder`}
                  </Button>
                </Grid>
              </Grid>
              <Grid container>
                <Grid item xs={6}>
                  <TextField
                    type="text"
                    name="firstname"
                    placeholder="Prénom"
                    defaultValue={data.firstname}
                    margin="normal"
                    variant="outlined"
                    required
                  />
                  <TextField
                    type="text"
                    name="lastname"
                    placeholder="Nom"
                    defaultValue={data.lastname}
                    margin="normal"
                    variant="outlined"
                    required
                  />

                  <TextField
                    disabled
                    type="email"
                    name="email"
                    placeholder="Email"
                    defaultValue={data.email}
                    margin="normal"
                    variant="outlined"
                    required
                  />
                  <TextField
                    type="text"
                    name="phone"
                    placeholder="Phone"
                    defaultValue={data.phone}
                    margin="normal"
                    variant="outlined"
                  />
                  <TextField
                    type="text"
                    name="address"
                    placeholder="Adress"
                    defaultValue={data.address}
                    margin="normal"
                    variant="outlined"
                  />
                  <TextField
                    type="text"
                    name="town"
                    placeholder="Ville"
                    defaultValue={data.town}
                    margin="normal"
                    variant="outlined"
                  />
                  <TextField
                    type="text"
                    name="postalCode"
                    placeholder="Postal Code"
                    defaultValue={data.postalCode}
                    margin="normal"
                    variant="outlined"
                  />
                  <TextField
                    // id="date"
                    name="dateBirth"
                    label="Date de naissance"
                    type="date"
                    defaultValue={
                      this.state.data.dateBirth !== null
                        ? ConvertDate(this.state.data.dateBirth)
                        : null
                    }
                    InputLabelProps={{
                      shrink: true
                    }}
                    margin="normal"
                    variant="outlined"
                  />
                  <TextField
                    type="text"
                    name="school"
                    placeholder="École"
                    defaultValue={data.school}
                    margin="normal"
                    variant="outlined"
                  />
                  <TextField
                    select
                    label="Level"
                    value={this.state.data.LevelStudyId || ""}
                    onChange={this.handleChange}
                    inputProps={{
                      name: "LevelStudyId"
                    }}
                    margin="normal"
                    variant="outlined"
                  >
                    {this.state.levelstudies.map(e => (
                      <MenuItem key={e.id} value={e.id}>
                        {e.label}
                      </MenuItem>
                    ))}
                  </TextField>

                  {/* <FormControl>
                  <InputLabel>Level</InputLabel>
                  <Select
                    value={this.state.data.LevelStudyId || ""}
                    onChange={this.handleChange}
                    inputProps={{
                      name: "LevelStudyId"
                    }}
                  >
                    {this.state.levelstudies.map(e => (
                      <MenuItem key={e.id} value={e.id}>
                        {e.label}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl> */}
                </Grid>
                <Grid item xs={6}>
                  <TextField
                    type="text"
                    name="titre"
                    placeholder="Intitulé de stage"
                    defaultValue={data.titre}
                    margin="normal"
                    variant="outlined"
                  />

                  <Grid container>
                    <Grid item xs={6}>
                      <TextField
                        // id="date"
                        name="dateStart"
                        label="Debut stage"
                        type="date"
                        defaultValue={
                          this.state.data.dateStart !== null
                            ? ConvertDate(this.state.data.dateStart)
                            : null
                        }
                        InputLabelProps={{
                          shrink: true
                        }}
                        margin="normal"
                        variant="outlined"
                      />
                    </Grid>
                    <Grid item xs={6}>
                      <TextField
                        // id="date"
                        name="dateEnd"
                        label="Fin stage"
                        type="date"
                        defaultValue={
                          this.state.data.dateEnd !== null
                            ? ConvertDate(this.state.data.dateEnd)
                            : null
                        }
                        InputLabelProps={{
                          shrink: true
                        }}
                        margin="normal"
                        variant="outlined"
                      />
                    </Grid>
                  </Grid>
                  <TextField
                    type="text"
                    multiline
                    name="description"
                    placeholder="Descriptions"
                    defaultValue={data.description}
                    margin="normal"
                    variant="outlined"
                    rows="5"
                  />
                </Grid>
              </Grid>
            </form>
          </div>

          <Dialog
            open={openTrainee}
            onClose={this.handleCloseTrainee}
            aria-labelledby="alert-dialog-title"
            aria-describedby="alert-dialog-description"
          >
            <DialogContent>
              <StudentView
                firstname={data.firstname}
                address={data.address}
                postalCode={data.postalCode}
                town={data.town}
                pictures={data.pictures}
                descriptionTrainee={data.description}
                school={data.school}
                titre={data.titre}
                dateStart={data.dateStart}
                dateEnd={data.dateEnd}
                size={FULL_RESTRICTED}
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={this.handleCloseTrainee} color="primary">
                {button}
              </Button>
            </DialogActions>
          </Dialog>
        </div>
      </MuiThemeProvider>
    );
  }
}
export default TraineeProfile;
